      { name: 'Product 2', category_id: 2 },
      { name: 'Product 3', category_id: 1 }
    ]);

    await knex('categories').insert([
      { name: 'Category A' },
      { name: 'Category B' }
    ]);

    // Join query to retrieve product details with categories
    const productsWithCategories = await knex('products')
      .select('products.name as product_name', 'categories.name as category_name')
      .leftJoin('categories', 'products.category_id', 'categories.id');

