import sqlite3

def generate_query(product_id, start_date, end_date):
    query = """
    SELECT c.customer_id, c.name, SUM(o.amount) as total_order_amount, AVG(o.amount) as avg_order_amount
    FROM Customers c
    JOIN Orders o ON c.customer_id = o.customer_id
    JOIN OrderItems oi ON o.order_id = oi.order_id
    WHERE oi.product_id = ? AND o.order_date BETWEEN ? AND ?
    GROUP BY c.customer_id
    HAVING COUNT(DISTINCT oi.product_id) = 1
    """

    params = (product_id, start_date, end_date)
    return query, params

