# Generate and execute the query
query, params = generate_query(product_id, start_date, end_date)
cursor.execute(query, params)

# Fetch and display results
results = cursor.fetchall()
for customer in results:
    print(f"Customer ID: {customer[0]}, Name: {customer[1]}, Total Order Amount: {customer[2]}, Avg Order Amount: {customer[3]}")

# Close the database connection
