```java
import java.util.ArrayList;
import java.util.List;

public class RecursiveQueryGenerator {

    public static void main(String[] args) {
        int departmentId = 100;
        int depth = 3;

        String query = generateHierarchicalQuery("employees", "id", "parent_id", "department_id", "title", departmentId, depth);
        System.out.println(query);
    }

    public static String generateHierarchicalQuery(String tableName, String idCol, String parentCol, String departmentCol, String titleCol, int departmentId, int depth) {
        StringBuilder query = new StringBuilder();
        query.append("WITH RECURSIVE hierarchy AS (\n");
        query.append("  SELECT id, ");
        query.append(titleCol).append(", ");
        query.append(departmentCol).append(", ");
        query.append("0 as depth, ");
        query.append(idCol).append(" as root_id\n");
        query.append("  FROM ").append(tableName).append("\n");
        query.append("  WHERE ").append(departmentCol).append(" = ?\n");
        query.append("  UNION ALL\n");
        query.append("  SELECT e.id, e.").append(titleCol).append(", e.").append(departmentCol).append(", h.depth + 1, e.").append(idCol).append("\n");
        query.append("  FROM ").append(tableName).append(" e, hierarchy h\n");
        query.append("  WHERE e.").append(idCol).append(" = h.parent_id AND e.").append(departmentCol).append(" = ? AND h.depth < ?\n");
        query.append(")\n");
        query.append("SELECT id, title, depth FROM hierarchy WHERE depth <= ?");
        return query.toString();
    }
}
```
