const fs = require('fs');
const path = require('path');

function generateEnvFile(data, env) {
    const templatePath = path.join(__dirname, `env_templates/${env}.template`);
    const outputPath = path.join(__dirname, '.env');

    fs.readFile(templatePath, 'utf8', (err, template) => {
        if (err) {
            console.error("Error reading template file:", err);
            return;
        }

        let envFileContent = template;
        Object.keys(data).forEach(key => {
            envFileContent = envFileContent.replace(`{{${key}}}`, data[key]);
        });

        fs.writeFile(outputPath, envFileContent, err => {
            if (err) {
                console.error("Error writing .env file:", err);
            } else {
                console.log(".env file generated successfully");
            }
        });
    });
}

