import json
from typing import Dict, Any, Type, TypeVar, Union, List, Optional
from dataclasses import dataclass, field, asdict

T = TypeVar('T')

def create_class_from_schema(schema: dict) -> Type[T]:
    class Meta(type):
        def __new__(metacls, name, bases, namespace):
            for key, value in schema.items():
                if isinstance(value, dict):
                    if 'oneOf' in value:
                        namespace[key] = field(default=None, metadata={'schema': value['oneOf']})
                    elif 'allOf' in value:
                        namespace[key] = field(default=None, metadata={'schema': [s for s in value['allOf']]})
                    elif 'anyOf' in value:
                        namespace[key] = field(default=None, metadata={'schema': value['anyOf']})
                    elif 'not' in value:
                        namespace[key] = field(default=None, metadata={'schema': [value['not']]})
                else:
                    namespace[key] = field(default=None)
            return super().__new__(metacls, name, bases, namespace)

    class JSONSchemaDataClass(metaclass=Meta):
        def __post_init__(self):
            self.validate()

        def validate(self):
            for attr, value in self.__dict__.items():
                if attr.startswith('_'):
                    continue
                schema = getattr(self, attr).metadata.get('schema', None)
                if schema:
                    for sub_schema in schema:
                        validate_data(value, sub_schema)

        def to_json(self) -> str:
            return json.dumps(asdict(self), indent=2)

