public class DocumentationCompressor {

    private static final double SIMILARITY_THRESHOLD = 0.7;

    public static void compressDocumentation(String inputFilePath, String outputFilePath) throws IOException {
        List<String> sections = readDocumentation(inputFilePath);
        Map<String, List<String>> mergedSections = new HashMap<>();

        for (String section : sections) {
            String normalizedSection = normalizeSection(section);
            if (!mergedSections.isEmpty()) {
                double maxSimilarity = -1;
                String mostSimilarKey = null;
                for (String key : mergedSections.keySet()) {
                    double similarity = calculateSimilarity(normalizedSection, key);
                    if (similarity > maxSimilarity) {
                        maxSimilarity = similarity;
                        mostSimilarKey = key;
                    }
                }
                if (maxSimilarity > SIMILARITY_THRESHOLD) {
                    mergedSections.get(mostSimilarKey).add(section);
                } else {
                    mergedSections.put(normalizedSection, new ArrayList<>(List.of(section)));
                }
            } else {
                mergedSections.put(normalizedSection, new ArrayList<>(List.of(section)));
            }
        }

        writeCompressedDocumentation(mergedSections, outputFilePath);
    }

    private static List<String> readDocumentation(String filePath) throws IOException {
        List<String> sections = new ArrayList<>();
        try (BufferedReader reader = new BufferedReader(new FileReader(filePath))) {
            String line;
            boolean newSection = true;
            StringBuilder currentSection = new StringBuilder();
            while ((line = reader.readLine()) != null) {
                if (line.isEmpty()) {
                    if (newSection && currentSection.length() > 0) {
                        sections.add(currentSection.toString());
                        currentSection.setLength(0);
                    }
                    newSection = true;
                } else {
                    currentSection.append(line).append("\n");
                    newSection = false;
                }
            }
            if (currentSection.length() > 0) {
                sections.add(currentSection.toString());
            }
        }
        return sections;
    }

    private static String normalizeSection(String section) {
