    }

    private static double calculateSimilarity(String section1, String section2) {
        CosineDistance cosine = new CosineDistance();
        JaccardSimilarity jaccard = new JaccardSimilarity();

        // Combine the two similarity measures (e.g., average)
        return (cosine.apply(section1.split(" "), section2.split(" ")) + jaccard.apply(section1, section2)) / 2;
    }

    private static void writeCompressedDocumentation(Map<String, List<String>> mergedSections, String filePath) throws IOException {
