    MemoryPool<Particle> particlePool;

    // Allocate all particles at once
    std::vector<Particle*> particles(num_particles);
    for (size_t i = 0; i < num_particles; ++i) {
        particles[i] = particlePool.allocate();
    }

    // Perform simulation (optimization details depend on the original code)
    for (size_t step = 0; step < num_steps; ++step) {
        for (Particle* particle : particles) {
            // ... update particle state, using optimized algorithms
        }
    }

    // Deallocate all particles at once
    for (Particle* particle : particles) {
        particlePool.deallocate(particle);
    }

    return 0;
}
