)

function Format-Size($bytes) {
    if ($bytes -lt 1KB) {
        return "{0} B" -f $bytes
    } elseif ($bytes -lt 1MB) {
        return "{0,3:F2} KB" -f ($bytes / 1KB)
    } elseif ($bytes -lt 1GB) {
