import os

def platform_specific_path(input_path):
    """
    Takes a path in Unix-style (forward slash-separated) and returns a platform-specific path.
    """
    if os.name == 'posix':
        # Unix-like systems (Linux, macOS)
        return input_path
    elif os.name == 'nt':
        # Windows
        return input_path.replace('/', '\\')
    elif os.name == 'java':
