Client-side (JavaScript, main.js):
```javascript
const codeEditor = document.getElementById('code-editor');
const peerConnections = new Map();
let localStream;

navigator.mediaDevices.getUserMedia({ video: false, audio: false })
    .then(stream => {
        localStream = stream;
        codeEditor.addEventListener('input', () => {
            sendTextChanges();
        });
    });

const sendTextChanges = () => {
    localStream.getTracks().forEach(track => track.enabled = false);
    peerConnections.forEach((pc, id) => {
        pc.send({ type: 'text', data: codeEditor.value });
    });
    localStream.getTracks().forEach(track => track.enabled = true);
};

const signalingServer = {
    addEventListener(type, callback) {
        // Implement signaling server events here, e.g., using WebSocket
    },
    send(data) {
        // Implement signaling server send function here, e.g., using WebSocket
    }
};

signalingServer.addEventListener('offer', (offer) => {
    const pc = new RTCPeerConnection();
    peerConnections.set(offer.from, pc);
    pc.setRemoteDescription(offer);
    pc.createAnswer().then(answer => pc.setLocalDescription(answer))
        .then(() => signalingServer.send({ type: 'answer', to: offer.from, desc: pc.localDescription }));
});

signalingServer.addEventListener('candidate', (candidate) => {
    peerConnections.get(candidate.from).addIceCandidate(candidate);
});

signalingServer.addEventListener('text', (data) => {
    console.log('Received text changes:', data);
    codeEditor.value = data.data;
});
```

