// Concept: Function
function isPalindrome(str) {
    // Concept: Control - Conditions
    if (typeof str !== 'string') {
        throw new TypeError("Input must be a string");
    }

    // Concept: Data - primitive types
    const alphanumericStr = str.toLowerCase().replace(/[^a-z0-9]/g, ''); // Remove non-alphanumeric characters and convert to lowercase

    // Concept: Control - Loops
    let start = 0;
    let end = alphanumericStr.length - 1;

    // Concept: Control - Loop condition
    while (start < end) {
        if (alphanumericStr[start] !== alphanumericStr[end]) {
            return false; // If characters at start and end don't match, it's not a palindrome
        }
        start++;
        end--;
    }

    return true; // If the loop completes without returning false, the string is a palindrome
}

