import com.github.javaparser.ast.CompilationUnit;
import com.github.javaparser.ast.body.MethodDeclaration;
import com.github.javaparser.ast.visitor.VoidVisitorAdapter;

public class CriticalSectionCommenter {

    public static void main(String[] args) {
        String code = // read the Java code as a string
        CompilationUnit cu = JavaParser.parse(code);

        cu.accept(new SynchronizationVisitor(), null);

        // Generate updated code with comments
        System.out.println(cu);
    }

    static class SynchronizationVisitor extends VoidVisitorAdapter<Void> {
        @Override
        public void visit(MethodDeclaration n, Void arg) {
            if (n.getAnnotations().stream().anyMatch(a -> a.getNameAsString().equals("synchronized"))) {
                n.addComment("CRITICAL SECTION: Synchronized method, potential for contention");
            }
            super.visit(n, arg);
        }
    }
}
