import ast

def remove_redundant_comments(file_path):
    modified_code = []
    with open(file_path, 'r') as file:
        lines = file.readlines()

    for line in lines:
        if line.startswith('#'):
            comment = line.strip().lstrip('#')
            if comment.lower() in [var_name.strip().lower() for var_name in extract_variable_names(file_path)]:
                continue
            if comment.lower() == line.split('=')[0].strip().lower():
                continue
            modified_code.append(line)
        else:
            modified_code.append(line)

    with open(file_path, 'w') as file:
        file.writelines(modified_code)

def extract_variable_names(file_path):
    with open(file_path, 'r') as file:
        tree = ast.parse(file.read())

    variable_names = []
    for node in ast.walk(tree):
        if isinstance(node, (ast.Name, ast.Attribute)):
            variable_names.append(node.id)
        elif isinstance(node, ast.Assign):
            for target in node.targets:
                if isinstance(target, (ast.Name, ast.Attribute)):
                    variable_names.append(target.id)

    return variable_names

# Example usage
