```python
import subprocess
from collections import defaultdict
from fuzzywuzzy import fuzz

def get_last_commits(n=100):
    repo_path = '/path/to/your/repository'
    command = f'git --no-pager log --no-merges --pretty=format:"%B" -n {n}'
    result = subprocess.run(command, cwd=repo_path, shell=True, capture_output=True, text=True)
    return result.stdout.split('\n\n')

def group_similar_commits(commits, threshold=85):
    commit_groups = defaultdict(list)
    for commit in commits:
        for group, commit_list in commit_groups.items():
            if fuzz.token_set_ratio(commit, group) >= threshold:
                commit_groups[group].append(commit)
                break
        else:
            commit_groups[commit] = [commit]
    return commit_groups

def summarize_commits(commit_groups):
    summaries = []
    for group, commit_list in commit_groups.items():
        if len(commit_list) > 1:
            summary = f"Multiple commits: {', '.join(commit_list[:3])}..."
        else:
            summary = group
        summaries.append(summary)
    return summaries

def main():
    commits = get_last_commits()
    grouped_commits = group_similar_commits(commits)
    summaries = summarize_commits(grouped_commits)
    for summary in summaries:
        print(summary)

if __name__ == "__main__":
    main()
```

