from typing import List, Tuple

def categorize_commits(commits):
    categories = {
        'Bug fixes': 'Fix',
        'Features': ['Add', 'Implement'],
        'Documentation': ['Docs', 'Update Readme'],
    }
    categorized = defaultdict(list)
    for commit in commits:
        for category, keywords in categories.items():
            for keyword in keywords:
                if keyword.lower() in commit.lower():
                    categorized[category].append(commit)
                    break
    return categorized

def count_categories(categorized_commits):
    return Counter([category for category in categorized_commits.keys()])

def write_changelog(summaries, categories, filename='changelog.md'):
    with open(filename, 'w') as f:
        for category, _ in sorted(categories.most_common(), key=lambda x: x[1], reverse=True):
            f.write(f"## {category}\n\n")
            for summary in categorized_commits[category]:
                f.write(f"- {summary}\n")
        f.write('\n## Others\n\n')
        for summary in summaries:
            if summary not in [s for c in categorized_commits.values() for s in c]:
                f.write(f"- {summary}\n")

