/**
 * Converts a JavaScript Date object into a specified string format.
 *
 * @param {Date} dateObj - The JavaScript Date object to be formatted.
 * @param {string} format - The desired output format string. Supports 'dd-mm-yyyy' and 'yyyy-mm-dd'.
 *
 * @returns {string} The formatted date string.
 * @throws {TypeError} If the input dateObj is not a Date object.
 * @throws {TypeError} If the format is not a string.
 * @throws {Error} If the format is not supported.
 *
 * @example
 * const date = new Date('2023-03-15T00:00:00Z');
 * convertDate(date, 'dd-mm-yyyy'); // Returns "15-03-2023"
 * convertDate(date, 'yyyy-mm-dd'); // Returns "2023-03-15"
 * convertDate(date, 'invalid_format'); // Throws Error: Invalid format.
 */
function convertDate(dateObj, format) {
    if (!(dateObj instanceof Date)) {
        throw new TypeError("Input must be a Date object.");
    }
    if (typeof format !== 'string') {
        throw new TypeError("Format must be a string.");
    }

    const supportedFormats = ['dd-mm-yyyy', 'yyyy-mm-dd'];
    if (!supportedFormats.includes(format)) {
        throw new Error("Invalid format.");
    }

    const pad = (num) => `0${num}`.slice(-2);
    const day = pad(dateObj.getDate());
    const month = pad(dateObj.getMonth() + 1);
    const year = dateObj.getFullYear();

    return format.replace(/dd/, day)
                  .replace(/mm/, month)
                  .replace(/yyyy/, year);
}
