template<typename T>
std::vector<T> matrixMultiply(const std::vector<T>& A, const std::vector<T>& B, size_t blockSize) {
    // Assuming square matrices with appropriate dimensions
    assert(A.size() / blockSize == B.size() / blockSize && A.size() % blockSize == 0 && B.size() % blockSize == 0);

    size_t N = A.size() / blockSize;
