                    for (size_t n = 0; n < blockSize; ++n) {
                        result[i * blockSize + m + (j * blockSize + n) * N] += A[i * blockSize + m] * B[k * blockSize + n + (j * blockSize + n) * N];
                    }
                }
            }
        }
    }

    return result;
}

