            key = f"rate_limit:{request.remote_addr}"
            current_time = time.time()
            previous_request = cache.get(key, 0)
            elapsed_time = current_time - previous_request

            if elapsed_time < 1 / per_second:
                remaining_time = (1 / per_second) - elapsed_time
                abort(429, {"Retry-After": remaining_time})
            else:
                cache.set(key, current_time)
                return func(*args, **kwargs)

        return wrapper

