            mergeSort(arr, left, mid);  // Corrected: Proper calculation of mid
            mergeSort(arr, mid + 1, right);  // Corrected: Proper range for the second half
            merge(arr, left, mid, right);
        }
    }

    // Corrected merge function implementation

    // Example usage:
    public static void main(String[] args) {
        int[] arr = {5, 3, 8, 1, 2};
        CorrectedMergeSort correctedSort = new CorrectedMergeSort();
        correctedSort.mergeSort(arr, 0, arr.length - 1);
        // Prints the sorted array with O(n log n) time complexity
        for (int num : arr) {
            System.out.print(num + " ");
        }
    }
}
