```go
package main

import (
	"crypto/tls"
	"fmt"
	"io/ioutil"
	"net/http"
)

func main() {
	// Load the server's certificate and private key
	cert, err := tls.LoadX509KeyPair("server.crt", "server.key")
	if err != nil {
		panic(err)
	}

	// Load the root CA certificate
	caCert, err := ioutil.ReadFile("ca.crt")
	if err != nil {
		panic(err)
	}
	caCertPool := x509.NewCertPool()
	caCertPool.AppendCertsFromPEM(caCert)

	// Configure the server's TLS settings
	tlsConfig := &tls.Config{
		Certificates: []tls.Certificate{cert},
		ClientAuth:   tls.RequireAndVerifyClientCert,
		RootCAs:      caCertPool,
	}

	// Create the server
	server := &http.Server{
		Addr:      ":8443",
		Handler:   http.HandlerFunc(handleRequest),
		TLSConfig: tlsConfig,
	}

	fmt.Println("Server is listening on port 8443...")
	server.ListenAndServeTLS("", "")
}

func handleRequest(w http.ResponseWriter, r *http.Request) {
	// Inspect the client certificate
	clientCert := r.TLS.PeerCertificates[0]
	fmt.Printf("Client certificate: %s\n", clientCert.Subject.CommonName)

	// Perform access control and revocation checks here

	w.Write([]byte("Secure request received!"))
}
```
