#include <iostream>

class Base {
public:
    Base() { std::cout << "Base constructed" << std::endl; }
    virtual ~Base() { std::cout << "Base destructed" << std::endl; }

    // Pure virtual function to force derived classes to implement their own cleanup
    virtual void cleanup() = 0;

protected:
    int* data = new int; // Simulating dynamic memory allocation
};

class Derived : public Base {
