# test_service_mesh.py
import asyncio
import time
import requests
from grpc.experimental.aio import Server, Client
from concurrent.futures import ThreadPoolExecutor
from service_protos import service_pb2_grpc, service_pb2
from random import randint, uniform
from urllib.parse import urlparse


class ServiceMock(service_pb2_grpc.ServiceServicer):
    def __init__(self, service_url):
        self.service_url = service_url

    async def CallService(self, request, context):
        # Simulate network latency
        await asyncio.sleep(uniform(0.1, 0.5))

        # Simulate packet loss
        if randint(0, 100) > 90:
            raise Exception("Network error")

        # Mock service response
        response_data = requests.get(f"{self.service_url}/mock").json()
        return service_pb2.Response(data=response_data)


def start_service_mock(service_mock, port=50051):
    server = Server([service_mock], port=port)
    server.start()
    return server


async def connect_to_service(service_url, mock_server=False):
    if mock_server:
        url_parts = urlparse(service_url)
        service_mock = ServiceMock(f"http://{url_parts.hostname}:{url_parts.port}")
        server = start_service_mock(service_mock)
        service_url = f"localhost:{server.server_port}"

    async with Client(service_url, insecure=True, loop=asyncio.get_event_loop()) as client:
        yield client


async def run_integration_test(service_urls, test_count=1000):
    tasks = []
    with ThreadPoolExecutor() as executor:
        for _ in range(test_count):
            task = asyncio.ensure_future(test_service_flow(service_urls, executor))
            tasks.append(task)

        await asyncio.gather(*tasks)


async def test_service_flow(service_urls, executor):
    async with connect_to_service(service_urls["service1"], mock_server=True) as client1:
        async with connect_to_service(service_urls["service2"]) as client2:
            for _ in range(10):
                await client1.CallService(service_pb2.Request())
                await client2.CallService(service_pb2.Request())


if __name__ == "__main__":
    service_urls = {
        "service1": "localhost:5001",
        "service2": "localhost:5002"
    }
    loop = asyncio.get_event_loop()
    loop.run_until_complete(run_integration_test(service_urls, 1000))
```

