import org.junit.Rule;
import org.junit.Test;
import org.junit.rules.TestRule;
import org.junit.rules.TestWatcher;
import org.mockito.Mockito;
import org.mockito.junit.MockitoJUnitRule;

import java.util.concurrent.CountDownLatch;

public class AsyncEventHandlerTest {
    @Rule
    public MockitoJUnitRule mockitoJUnitRule = new MockitoJUnitRule(this);
    private AsyncEventHandler handler;
    private EventService eventService;

    @Before
    public void setUp() {
        handler = new AsyncEventHandler();
        eventService = Mockito.mock(EventService.class);
        handler.setEventService(eventService);
    }

    @Test
    public void testHandleEventAsync_withValidEvent() throws InterruptedException {
        final CountDownLatch latch = new CountDownLatch(1);
        Event event = new Event("validEvent");
        handler.handleEventAsync(event, result -> {
            Mockito.verify(eventService).processEvent(event);
            latch.countDown();
        });

        latch.await(); // Wait for async task to complete
    }

    @Test
    public void testHandleErrorWithInvalidEvent() {
        Event invalidEvent = new Event("invalidEvent");
        handler.handleError(invalidEvent, new Exception("Invalid event"));

        Mockito.verify(eventService, Mockito.never()).processEvent(invalidEvent);
    }
}
```

