const esprima = require('esprima');
const escodegen = require('escodegen');
const xssPatterns = require('./xss_patterns');
const sqlInjectionPatterns = require('./sql_injection_patterns');

function detectXSS(code) {
    const ast = esprima.parseScript(code, { tolerant: true, loc: true });
    const xssIssues = [];

    ast.body.forEach(node => {
        if (node.type === 'ExpressionStatement' && node.expression.type === 'CallExpression') {
            const functionName = node.expression.callee.name;
            const functionArgs = node.expression.arguments.map(arg => arg.value);

            xssPatterns.forEach(pattern => {
                if (pattern.name === functionName && pattern.args.includes(functionArgs.length)) {
                    xssIssues.push({ location: node.loc.start.line, description: pattern.warning });
                }
            });
        }
    });

    return xssIssues;
}

function detectSQLInjection(code) {
    const ast = esprima.parseScript(code, { tolerant: true, loc: true });
    const sqlInjectionIssues = [];

    traverse(ast, node => {
        if (node.type === 'ExpressionStatement' && node.expression.type === 'CallExpression') {
            const functionName = node.expression.callee.name;
            const functionArgs = node.expression.arguments.map(arg => arg.value);

            sqlInjectionPatterns.forEach(pattern => {
                if (pattern.name === functionName && pattern.args.includes(functionArgs.length)) {
                    sqlInjectionIssues.push({ location: node.loc.start.line, description: pattern.warning });
                }
            });
        }
    });

    return sqlInjectionIssues;
}

