def enforce_code_style(code):
    import re
    from textwrap import dedent

    # Variable and function naming convention: snake_case
    snake_case_pattern = re.compile(r'^[a-z_][a-z0-9_]*$', re.IGNORECASE)
    if not all(snake_case_pattern.match(name) for name in re.findall(r'\b\w+\b', code)):
        return "Error: Variables and functions should be in snake_case."

    # Function names should start with a lowercase letter
    func_name_pattern = re.compile(r'^[a-z]\w*\(.*\)$', re.IGNORECASE | re.MULTILINE)
    if not all(func_name_pattern.match(func) for func in re.findall(r'^\s*def\s+(\w+)', code, re.MULTILINE)):
        return "Error: Function names should start with a lowercase letter."

    # Check for proper indentation (4 spaces)
    dedented_code = dedent(code)
    if '\t' in dedented_code or any(len(line) % 4 != 0 for line in dedented_code.splitlines()):
        return "Error: Use 4 spaces for indentation, no tabs."

